import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const API_KEY = "0acee91c28af4153bea185233251802"; // Replace with your actual WeatherAPI key

  const fetchWeather = async () => {
    if (!city) return;

    try {
      setError(""); // Reset error state
      const response = await axios.get(
        `https://api.weatherapi.com/v1/current.json?key=${API_KEY}&q=${city}`
      );
      setWeather(response.data);
    } catch (error) {
      setWeather(null); // Reset weather state if error occurs
      setError("City not found! Please try again.");
      console.error("Error fetching weather:", error.response?.data || error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-r from-blue-500 to-purple-700 p-6 text-white">
      <h1 className="text-5xl font-extrabold mb-6">Weather App</h1>
      <div className="bg-white p-6 rounded-xl shadow-lg flex flex-col items-center w-80">
        <input
          type="text"
          placeholder="Enter city name..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="p-3 border rounded-lg w-full text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={fetchWeather}
          className="mt-4 px-6 py-3 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700 transition duration-300"
        >
          Get Weather
        </button>

        {error && <p className="text-red-500 mt-4">{error}</p>}

        {weather && weather.location && (
          <div className="mt-6 p-6 bg-blue-100 text-gray-900 rounded-lg shadow-md w-full text-center">
            <h2 className="text-2xl font-semibold text-blue-900">
              {weather.location.name}, {weather.location.country}
            </h2>
            <p className="text-lg text-gray-700">{weather.location.region}</p>
            <img
              src={`https:${weather.current.condition.icon}`}
              alt={weather.current.condition.text}
              className="mx-auto w-20 h-20"
            />
            <p className="text-3xl font-bold text-blue-900">{weather.current.temp_f}¬∞F</p>
            <p className="text-lg font-medium text-gray-700">{weather.current.condition.text}</p>
            <p className="text-sm">Feels like: {weather.current.feelslike_f}¬∞F</p>
            <div className="mt-4 flex justify-between w-full text-sm text-gray-600">
              <p>üí® Wind: {weather.current.wind_mph} mph ({weather.current.wind_dir})</p>
              <p>üíß Humidity: {weather.current.humidity}%</p>
            </div>
            <div className="mt-2 flex justify-between w-full text-sm text-gray-600">
              <p>üå°Ô∏è Pressure: {weather.current.pressure_mb} mb</p>
              <p>üëÅÔ∏è Visibility: {weather.current.vis_km / 1} Miles</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
